import assert from 'assert';
import Range from './Range.js';
import backwardRangeIterator from './backwardRangeIterator.js';

export default class ReverseRange extends Range {
	/**
	 * Constructor.
	 *
	 * @param {number} start
	 * @param {number} stop
	 * @param {number} step
	 */
	constructor(start, stop, step) {
		assert(step !== 0);
		assert(step < 0);
		super(
			start,
			stop,
			step,
			start <= stop ? 0 : 1 + Math.floor((stop - start + 1) / step),
		);
	}

	/**
	 * @returns {IterableIterator<number>}
	 */
	[Symbol.iterator]() {
		return backwardRangeIterator(this.start, this.stop, this.step);
	}

	/**
	 * Has.
	 *
	 * @param {number} x
	 * @returns {boolean}
	 */
	has(x) {
		if (!Number.isInteger(x)) return false;
		if (x <= this.stop) return false;
		if (x > this.start) return false;
		return (this.start - x) % this.step === 0;
	}
}

import assert from 'assert';
import Range from './Range.js';
import forwardRangeIterator from './forwardRangeIterator.js';

export default class ForwardRange extends Range {
	/**
	 * Constructor.
	 *
	 * @param {number} start
	 * @param {number} stop
	 * @param {number} step
	 */
	constructor(start, stop, step) {
		assert(step !== 0);
		assert(step > 0);
		super(
			start,
			stop,
			step,
			start >= stop ? 0 : 1 + Math.floor((stop - start - 1) / step),
		);
	}

	/**
	 * @returns {IterableIterator<number>}
	 */
	[Symbol.iterator]() {
		return forwardRangeIterator(this.start, this.stop, this.step);
	}

	/**
	 * Has.
	 *
	 * @param {number} x
	 * @returns {boolean}
	 */
	has(x) {
		if (!Number.isInteger(x)) return false;
		if (x >= this.stop) return false;
		if (x < this.start) return false;
		return (x - this.start) % this.step === 0;
	}
}

import assert from 'assert';

export default class Range {
	/**
	 * Constructor.
	 *
	 * @param {number} start
	 * @param {number} stop
	 * @param {number} step
	 * @param {number} length
	 */
	constructor(start, stop, step, length) {
		assert(Number.isInteger(start));
		assert(Number.isInteger(stop));
		assert(Number.isInteger(step));
		assert(Number.isInteger(length));
		this.start = start;
		this.stop = stop;
		this.step = step;
		this.length = length;
	}

	/**
	 * @returns {string}
	 */
	toString() {
		return `range(${this.start}, ${this.stop}, ${this.step})`;
	}

	/**
	 * Get.
	 *
	 * @param {number} i
	 * @returns {number}
	 */
	get(i) {
		if (!Number.isInteger(i)) throw new Error('IndexError');
		if (i < 0) throw new Error('IndexError');
		if (i >= this.length) throw new Error('IndexError');
		return this.start + i * this.step;
	}
}

import assert from 'assert';

/**
 * Yields values in a range, separated by a fixed negative constant called
 * step. The range has to be given in reverse order, that is, largest value
 * first, smallest value second. This is an iterator that preserves state
 * accross calls.
 *
 * @param {number} start - The starting value.
 * @param {number} stop - The stopping value.
 * @param {number} step - The step value (<0).
 * @returns {IterableIterator<number>}
 */
export default function* backwardRangeIterator(start, stop, step) {
	assert(step < 0);
	for (; start > stop; start += step) yield start;
}

import assert from 'assert';

/**
 * Yields values in a range, separated by a fixed positive constant called
 * step. This is an iterator that preserves state accross calls.
 *
 * @param {number} start - The starting value.
 * @param {number} stop - The stopping value.
 * @param {number} step - The step value (>0).
 * @returns {IterableIterator<number>}
 */
export default function* forwardRangeIterator(start, stop, step) {
	assert(step > 0);
	for (; start < stop; start += step) yield start;
}

export {default as BackwardRange} from './BackwardRange.js';
export {default as ForwardRange} from './ForwardRange.js';
export {default as Range} from './Range.js';
export {default as backwardRangeIterator} from './backwardRangeIterator.js';
export {default as forwardRangeIterator} from './forwardRangeIterator.js';
export {default as range} from './range.js';

import ForwardRange from './ForwardRange.js';
import BackwardRange from './BackwardRange.js';

/**
 * Yields values in a range, separated by a fixed constant called step. If this
 * step is negative, the range has to be given in reverse order, that is,
 * largest value first, smallest value second. Both the starting value and the
 * step value are optional. By default the starting value is <code>0</code>.
 * The default for the step value is <code>1</code>.
 *
 * @param {number} [start=0] - The starting value.
 * @param {number} stop - The stopping value.
 * @param {number} [step=1] - The step value.
 * @returns {ForwardRange|BackwardRange}
 */
const range = (start, stop, step) => {
	if (stop === undefined) {
		return new ForwardRange(0, start, 1);
	}

	if (step === undefined) {
		return new ForwardRange(start, stop, 1);
	}

	return step < 0
		? new BackwardRange(start, stop, step)
		: new ForwardRange(start, stop, step);
};

export default range;

